// ============================================
// Helper Union Types
// ============================================

/**
 * Union of all valid schema names (kebab-case file names)
 */
export type SURefSchemaName =
  | 'abilities'
  | 'ability-tree-requirements'
  | 'bio-titans'
  | 'chassis'
  | 'classes.advanced'
  | 'classes.core'
  | 'classes.hybrid'
  | 'crawler-bays'
  | 'crawler-tech-levels'
  | 'crawlers'
  | 'creatures'
  | 'drones'
  | 'equipment'
  | 'keywords'
  | 'meld'
  | 'modules'
  | 'npcs'
  | 'roll-tables'
  | 'squads'
  | 'systems'
  | 'traits'
  | 'vehicles'

/**
 * Union of all file-level schema entity types
 */
export type SURefEntity =
  | SURefAbility
  | SURefAbilityTreeRequirement
  | SURefAdvancedClass
  | SURefBioTitan
  | SURefChassis
  | SURefCoreClass
  | SURefCrawler
  | SURefCrawlerBay
  | SURefCrawlerTechLevel
  | SURefCreature
  | SURefDrone
  | SURefEquipment
  | SURefHybridClass
  | SURefKeyword
  | SURefMeld
  | SURefModule
  | SURefNpc
  | SURefRollTable
  | SURefSquad
  | SURefSystem
  | SURefTrait
  | SURefVehicle

/**
 * Union of all valid meta schema names (includes actions)
 */
export type SURefMetaSchemaName = SURefSchemaName | 'actions'

/**
 * Union of all meta entity types (includes actions)
 */
export type SURefMetaEntity = SURefEntity | SURefMetaAction
